using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using static System.Runtime.InteropServices.JavaScript.JSType;

namespace AdventOfCode.Puzzle
{
    public class Hand : IComparable<Hand>
    {
        public static List<char> alphabet = new List<char>() { 'J', '2', '3', '4', '5', '6', '7', '8', '9', 'T', 'Q', 'K', 'A' };
        public static int[] pows13 = { 1, 13, 169, 2197, 28561 };

        public string Cards { get; set; }
        public string ToChoose { get; set; }
        public int Bid { get; set; }
        public int Value { get; set; }

        public Hand(string cards, int bid)
        {
            this.Cards = cards;
            this.Bid = bid;

            for (int i = 0; i < this.Cards.Length; i++)
                this.Value += (int)(alphabet.IndexOf(this.Cards[i]) * pows13[this.Cards.Length - i - 1]);
        }

        public int CompareTo(Hand? other)
        {
            return this.Value.CompareTo(other!.Value);
        }
    }
    internal class Day07 : IDayPuzzle
    {
        public string ExecutePart1()
        {
            List<Hand> fiveof = new List<Hand>();
            List<Hand> fourof = new List<Hand>();
            List<Hand> fullhouse = new List<Hand>();
            List<Hand> threeof = new List<Hand>();
            List<Hand> twopair = new List<Hand>();
            List<Hand> onepair = new List<Hand>();
            List<Hand> thefool = new List<Hand>();

            foreach (Hand hand in input)
            {
                var groups = hand.Cards.ToArray().GroupBy(card => card)
                        .Select(group => new
                        {
                            Card = group.Key,
                            Count = group.Count()
                        });

                if (groups.Count() == 1) fiveof.Add(hand);
                else if (groups.Count() == 2 && (groups.First().Count == 1 || groups.First().Count == 4)) fourof.Add(hand);
                else if (groups.Count() == 2 && (groups.First().Count == 2 || groups.First().Count == 3)) fullhouse.Add(hand);
                else if (groups.Count() == 3 && groups.Any(g => g.Count == 3)) threeof.Add(hand);
                else if (groups.Count() == 3 && groups.Count(g => g.Count == 2) == 2) twopair.Add(hand);
                else if (groups.Count() == 4) onepair.Add(hand);
                else if (groups.Count() == 5) thefool.Add(hand);
            }

            fiveof.Sort();
            fourof.Sort();
            fullhouse.Sort();
            threeof.Sort();
            twopair.Sort();
            onepair.Sort();
            thefool.Sort();

            long sum = 0;
            int rank = 1;
            for (int i = 0; i < thefool.Count; i++) sum += thefool[i].Bid * (rank++);
            for (int i = 0; i < onepair.Count; i++) sum += onepair[i].Bid * (rank++);
            for (int i = 0; i < twopair.Count; i++) sum += twopair[i].Bid * (rank++);
            for (int i = 0; i < threeof.Count; i++) sum += threeof[i].Bid * (rank++);
            for (int i = 0; i < fullhouse.Count; i++) sum += fullhouse[i].Bid * (rank++);
            for (int i = 0; i < fourof.Count; i++) sum += fourof[i].Bid * (rank++);
            for (int i = 0; i < fiveof.Count; i++) sum += fiveof[i].Bid * (rank++);

            return sum.ToString();
        }
        public string ExecutePart2()
        {
            //Plus ou moins la même que la partie 1 en fait, mais je veux pas l'écraser

            List<Hand> fiveof = new List<Hand>();
            List<Hand> fourof = new List<Hand>();
            List<Hand> fullhouse = new List<Hand>();
