            List<Hand> thefool = new List<Hand>();

            foreach (Hand hand in input)
            {
                var groups = hand.Cards.ToArray().GroupBy(card => card)
                        .Select(group => new
                        {
                            Card = group.Key,
                            Count = group.Count()
                        });

                char bestcardwithoutJ = groups.Where(g => g.Card != 'J').OrderByDescending(g => g.Count).FirstOrDefault()?.Card ?? 'A';
